import { useEffect, useState } from "react";

export default function MyApplications() {
  const [apps, setApps] = useState([]);

  useEffect(() => {
    const userId = localStorage.getItem("userId"); // ✅ real id from login
    if (!userId) return;

    fetch(`http://localhost:5000/api/applications/my/${userId}`)
      .then((res) => res.json())
      .then((data) => setApps(data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div>
      <h2>My Applications</h2>
      <ul>
        {apps.map((a) => (
          <li key={a._id}>
            {a.note} ({a.resumeLink})
          </li>
        ))}
      </ul>
    </div>
  );
}
