import { useEffect, useState } from 'react';

export default function Jobs() {
  const [jobs, setJobs] = useState([]);
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');
  const [location, setLocation] = useState('');
  const [salary, setSalary] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    fetch('localhost/jobs')
      .then(res => res.json())
      .then(data => setJobs(data));
  }, []);

  const handlePost = async () => {
    const res = await fetch(import.meta.env.VITE_API + '/jobs', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ title, company, location, salary, description })
    });
    const job = await res.json();
    setJobs([...jobs, job]);
  };

  return (
    <div>
      <h2>Jobs</h2>
      <div>
        <input placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} />
        <input placeholder="Company" value={company} onChange={e => setCompany(e.target.value)} />
        <input placeholder="Location" value={location} onChange={e => setLocation(e.target.value)} />
        <input placeholder="Salary" value={salary} onChange={e => setSalary(e.target.value)} />
        <input placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} />
        <button onClick={handlePost}>Post Job</button>
      </div>
      <ul>
        {jobs.map(j => (
          <li key={j._id}>{j.title} - {j.company}</li>
        ))}
      </ul>
    </div>
  );
}
