import { useEffect, useState } from "react";

export default function Jobs() {
  const [jobs, setJobs] = useState([]);
  const [title, setTitle] = useState("");
  const [company, setCompany] = useState("");
  const [location, setLocation] = useState("");
  const [salary, setSalary] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/jobs")
      .then((res) => res.json())
      .then((data) => setJobs(data));
  }, []);

  const handlePost = async () => {
    const token = localStorage.getItem("token");
    if (!token) return alert("Login as admin to post jobs");

    const res = await fetch("http://localhost:5000/api/jobs", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: token,
      },
      body: JSON.stringify({ title, company, location, salary, description }),
    });
    const job = await res.json();
    if (res.ok) {
      setJobs([...jobs, job]);
    } else {
      alert(job.error || "Failed to post job");
    }
  };

  const handleApply = async (jobId) => {
    const token = localStorage.getItem("token");
    const userId = localStorage.getItem("userId");
    if (!token) return alert("Login to apply for jobs");

    const resumeLink = prompt("Enter Resume Link:");
    const note = prompt("Enter Note:");

    const res = await fetch("http://localhost:5000/api/applications", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: token,
      },
      body: JSON.stringify({ jobId, userId, resumeLink, note }),
    });
    const data = await res.json();
    if (res.ok) alert("Applied successfully!");
    else alert(data.error || "Failed to apply");
  };

  return (
    <div>
      <h2>Jobs</h2>
      <div>
        <input placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} /><br/>
        <input placeholder="Company" value={company} onChange={(e) => setCompany(e.target.value)} />
        <input placeholder="Location" value={location} onChange={(e) => setLocation(e.target.value)} />
        <input placeholder="Salary" value={salary} onChange={(e) => setSalary(e.target.value)} />
        <input placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
        <button onClick={handlePost}>Post Job</button>
      </div>
      <ul>
        {jobs.map((j) => (
          <li key={j._id}>
            {j.title} - {j.company} 
            <button onClick={() => handleApply(j._id)}>Apply</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
